import { useState, useRef } from "react";
import './Register.css';



function Register(props) {

    const nameRef = useRef(null);
    const userRef = useRef(null);
    const passRef = useRef(null);
    const mailRef = useRef(null);

    const [client, setClient] = useState({
        "userName":"",
        "name": "",
        "password": "",
        "ADMIN":0,
        "MAIL":""
       
    });
    
    
    const onRegister = (e) => {
        e.preventDefault();
        
        setClient({
            "name":nameRef.current.value,
            "user": userRef.current.value,
            "password": passRef.current.value,
            "mail":mailRef.current.value
     
        });


        let url = 'https://g5ab0d028fce44a-proyecto.adb.us-phoenix-1.oraclecloudapps.com/ords/proyecto/proyecto/register/'
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json' },
            body: JSON.stringify({
                "userName":nameRef.current.value,
                "name":userRef.current.value,
                "password":passRef.current.value,
                "ADMIN":0,
                "MAIL":mailRef.current.value
            })
        };
        fetch(url, requestOptions)
            .then(async response => {
                alert(response)
                if (response.ok) {
                    return response.json();
                }
                const errorData = await response.json();
                return Promise.reject(errorData);
            })



    }

    const outRegister = (e) => {
        e.preventDefault();
        props.setRegister({
            ...props.player,
            "register": false,
            "message": "",
        })
        }

   
    return(

        <div className="register-main">
            <h3>Registrar</h3>
        
            <div className="field-group-register">
                <label htmlFor="name">Nombre</label>
                <input ref={nameRef} type="text" name="name" id="name"/>

                 <label htmlFor="user">Nombre de usuario</label>
                <input ref={userRef} type="text" name="pass" id="pass"/>


                <label htmlFor="pass">Contrase√±a</label>
                <input ref={passRef} type="text" name="pass" id="pass"/>

                <label htmlFor="mail">Correo</label>
                <input ref={mailRef} type="text" name="name" id="name"/>


          
            </div>
            <button className="main-btn" onClick={e => onRegister(e)}>Registrar</button>

            <div className="session">
                < button className="session-btn" onClick={e => outRegister(e)} >Salir</button>
            </div>

        </div>
        


        
    )
}

export default Register;
